# Ottieni il progetto corrente
project = QgsProject.instance()

# Itera attraverso la lista di relazioni e crea ciascuna relazione
for relazione in relazioni:
    # Ottieni i layer padre e figlio dal progetto
    layer_padre = project.mapLayersByName(relazione["nome_layer_padre"])[0]
    layer_figlio = project.mapLayersByName(relazione["nome_layer_figlio"])[0]
    
    # Crea una nuova relazione
    relation = QgsRelation()
    relation.setName(relazione["nome_relazione"])  # Nome della relazione
    relation.setId(f'relazione_{relazione["nome_relazione"]}')  # ID univoco per la relazione
    relation.setReferencingLayer(layer_figlio.id())  # Layer figlio (referencing)
    relation.setReferencedLayer(layer_padre.id())  # Layer padre (referenced)
    relation.addFieldPair(relazione["chiave_padre"], relazione["chiave_figlio"])  # Aggiunge la relazione tra i campi
    
    # Aggiungi la relazione al progetto
    project.relationManager().addRelation(relation)
    
    print(f'Relazione {relazione["nome_relazione"]} tra {relazione["nome_layer_padre"]} e {relazione["nome_layer_figlio"]} creata con successo!')

# Salva il progetto per applicare tutte le relazioni
project.write()
print("Tutte le relazioni sono state create e salvate.")
